mapper.select:
SELECT
COUNT(DISTINCT zone_uuid) AS count
FROM
measure_list_issue
WHERE
delete_at IS NULL
AND project_id = #{projectId}
AND list_id = #{listId}
AND close_status = #{unclosecode}

SELECT
COUNT(id) AS count
FROM
measure_list_issue
WHERE
delete_at IS NULL
AND project_id = #{projectId}
AND list_id = #{listId}
AND typ = #{typ}

SELECT
COUNT(id) AS count
FROM
measure_list_issue
WHERE
delete_at IS NULL
AND project_id = #{projectId}
AND list_id = #{listId}
AND `status` = #{status}
