SELECT `status`, COUNT( * ) AS count FROM measure_list_issue WHERE delete_at IS NULL AND category_path_and_key LIKE concat(#{cateChildPath},'%') AND area_path_and_id LIKE concat(#{areaChilePath},'%') AND close_status <> #{closedcode} AND list_id IN
#{item}
GROUP BY `status`

select id, area_id, area_path_and_id, attachment_md5_list, category_key, category_path_and_key, repairer_id, sender_id, squad_id, close_status, close_time, close_user, `condition`, `desc`, drawing_md5, destroy_at, destroy_user, plan_end_on, end_on, last_assigner, last_assigner_at, list_id, pos_x, pos_y, project_id, `status`, typ, uuid, region_uuid, zone_uuid, client_create_at, create_at, update_at, delete_at from measure_list_issue where list_id=#{list_id} and delete_at is null
AND update_at > FROM_UNIXTIME( #{timestamp} )
limit #{start},#{limit}
