mapper.select:
SELECT

FROM
	measure_region
WHERE
	project_id = #{projectId}

      AND update_at = #{updateAtGte}

SELECT

FROM
	measure_region
WHERE
	project_id = #{projectId}
	AND id > #{lastId}

	AND update_at > FROM_UNIXTIME( #{timestamp} )


      ORDER BY
      id ASC
      LIMIT #{start},#{measureApiGetPerTime}
