AND category_path_and_key like concat('%',#{categoryKey},'%')
GROUP BY squad_id

SELECT DISTINCT category_path_and_key FROM measure_zone_result WHERE list_id = #{listId}
AND category_key like concat('%',#{categoryKey},'%')
AND delete_at IS NULL

